mapper.select:
select 

from quota_month
where `date` = #{date,jdbcType=TIMESTAMP}
  and plantId = #{plantid,jdbcType=INTEGER}

mapper.delete:
delete from quota_month
where `date` = #{date,jdbcType=TIMESTAMP}
  and plantId = #{plantid,jdbcType=INTEGER}

mapper.update:
update quota_month
set powerGeneration = #{powergeneration,jdbcType=DOUBLE},
  downTimeReducation = #{downtimereducation,jdbcType=DOUBLE},
  powerReducation = #{powerreducation,jdbcType=DOUBLE},
  otherReducation = #{otherreducation,jdbcType=DOUBLE},
  downtime = #{downtime,jdbcType=BIGINT}
where `date` = #{date,jdbcType=TIMESTAMP}
  and plantId = #{plantid,jdbcType=INTEGER}
